import re
import time
import json
from Plugin import PluginManager
from Config import config
#-----------------------------------------------------------------------------------
def jsonStore(data, fileName):
    with open(fileName, 'w') as json_file:
        json_file.write(json.dumps(data))
def jsonLoad(fileName):
    with open(fileName) as json_file:
        data = json.load(json_file)
        return data
#-----------------------------------------------------------------------------------
@PluginManager.registerTo("SiteManager")
class SiteManagerPlugin(object):
    site_zeroname = None
    local_address = None
    db_domains = None
    db_domains_modified = None
#-----------------------------------------------------------------------------------
    def isDomain(self, address):
        return re.match("(.*?)([A-Za-z0-9_-]+\.[A-Za-z0-9]+)$", address)
    def isAddress(self, address):
        if self.isDomain(address):
            return True
        else:
            return super(SiteManagerPlugin, self).isAddress(address)
#-----------------------------------------------------------------------------------
    def need(self, address, all_file=True):
        if self.isDomain(address):
            if self.resolveDomain(address):
                address = self.resolveDomain(address)
            else:
                return None
        return super(SiteManagerPlugin, self).need(address, all_file)
#-----------------------------------------------------------------------------------
    def get(self, address):
        if self.sites is None:
            self.load()
        if self.isDomain(address):
            if self.resolveDomain(address):
                site = self.sites.get(self.resolveDomain(address))
                if site:
                    site_domain = site.settings.get("domain")
                    if site_domain != address:
                        site.settings["domain"] = address
            else:
                site = self.sites.get(address)
        else:
            site = self.sites.get(address)
        return site
#-----------------------------------------------------------------------------------
    def load(self, *args, **kwargs):
        super(SiteManagerPlugin, self).load(*args, **kwargs)
        if not self.get(config.bit_resolver):
            self.need(config.bit_resolver)
#-----------------------------------------------------------------------------------
    def resolveDomain(self, domain):
        domain = domain.lower()
        if not self.site_zeroname:
            self.site_zeroname = self.need(config.bit_resolver)
        site_zeroname_modified = self.site_zeroname.content_manager.contents.get("content.json", {}).get("modified", 0)
        if not self.db_domains or self.db_domains_modified != site_zeroname_modified:
            self.site_zeroname.needFile("data/names.json", priority=10)
            s = time.time()
            self.db_domains = self.site_zeroname.storage.loadJson("data/names.json")
            self.db_domains_modified = site_zeroname_modified
        self.local_address = jsonLoad(config.addressbook_name)
        if self.db_domains.get(domain):
            return self.db_domains.get(domain)
        else:
            return self.local_address.get(domain)
#-----------------------------------------------------------------------------------
@PluginManager.registerTo("UiRequest")
class UiRequestPlugin(object):
    address_book = None
    def __init__(self, *args, **kwargs):
        from Site import SiteManager
        self.site_manager = SiteManager.site_manager
        super(UiRequestPlugin, self).__init__(*args, **kwargs)
#-----------------------------------------------------------------------------------
    def actionSiteMedia(self, path, header_length=True):
        match = re.match("/media/(?P<address>[A-Za-z0-9-]+\.[A-Za-z0-9\.-]+)(?P<inner_path>/.*|$)", path)
        if match:
            domain = match.group("address")
            address = self.site_manager.resolveDomain(domain)
            if address:
                path = "/media/" + address + match.group("inner_path")
        return super(UiRequestPlugin, self).actionSiteMedia(path, header_length=header_length)
#-----------------------------------------------------------------------------------
    def isMediaRequestAllowed(self, site_address, referer):
        referer_path = re.sub("http[s]{0,1}://.*?/", "/", referer).replace("/media", "")
        referer_path = re.sub("\?.*", "", referer_path)
        if not re.sub("^http[s]{0,1}://", "", referer).startswith(self.env["HTTP_HOST"]):
            return False
        if self.isProxyRequest():
            referer = re.sub("^http://zero[/]+", "http://", referer)
            referer_site_address = re.match("http[s]{0,1}://(.*?)(/|$)", referer).group(1)
        else:
            referer_site_address = re.match("/(?P<address>[A-Za-z0-9\.-]+)(?P<inner_path>/.*|$)", referer_path).group("address")
        if referer_site_address == site_address:
            return True
        elif self.site_manager.resolveDomain(referer_site_address) == site_address:
            return True
        else:
            return False
#-----------------------------------------------------------------------------------
    def actionAddressBook(self):
        self.sendHeader()
        posted = self.getPosted()
        address_book = jsonLoad(config.addressbook_name)
        yield """<tt><form method="post"><h1>Address Book</h1><h2>Site list:</h2><ol>"""
        for Domain in address_book.keys():
            yield "<li><input type=\"checkbox\" name=\"delete-" + Domain + "\"/><a href=\"/" + Domain + "\"><tt>" + Domain + "</tt></a> -&gt; <a href=\"/" + str(address_book[Domain]) + "\"><tt>" + str(address_book[Domain]) + "</tt></a></li>"
        yield """</ol><h2>ZeroNet site to resolve domains:</h2>"""
        yield "<ol><li><a href=\"/" + config.bit_resolver + "\"><tt>" + config.bit_resolver + "</tt></a></li></ol>"
        yield """<h2>Add new record:</h2><ol><li><input type="text" placeholder="example.domain.bit" name="addDomain"/> -&gt; <input type="text" placeholder="1ExampleAddress1111111111111111111" size="40" name="addAddress"></li></ol><input type="submit" value="Add record and delete selected records"/>"""
        if posted:
            yield "<ol>"
            for Domain in address_book.keys():
                if posted.get("delete-" + Domain):
                    yield "<li>Deleted " + Domain + " -&gt; " + str(address_book[Domain]) + "</li>"
                    address_book.pop(Domain)
            if posted.get("addDomain") == "":
                yield ""
            elif posted.get("addAddress") == "":
                yield ""
            else:
                address_book[posted.get("addDomain")] = posted.get("addAddress")
                yield "<li>Added " + str(posted.get("addDomain")) + " -&gt; " + str(posted.get("addAddress")) + "</li>"
            yield "</ol>"
            jsonStore(address_book, config.addressbook_name)
        else:
            yield "<br/>"
        yield """<a href="https://github.com/2047/someZeroNetPlugins/tree/master/AddressBookPlugin"><tt>GitHub</tt></a></form></tt>"""
#-----------------------------------------------------------------------------------
@PluginManager.registerTo("ConfigPlugin")
class ConfigPlugin(object):
    def createArguments(self):
        group = self.parser.add_argument_group("Address book plugin")
        group.add_argument('--bit_resolver', help='ZeroNet site to resolve .bit domains', default="1Name2NXVi1RDPDgf5617UoW7xA6YrhM9F", metavar="address")
        group.add_argument('--addressbook_name', help='Address book file name', default="address.json.txt", metavar="fileName")
        return super(ConfigPlugin, self).createArguments()

